// by. MiohitoKiri5474
#include<bits/stdc++.h>

#pragma GCC optimize ( "O3" )
#pragma loop_opt ( on )

using namespace std;

#define maxN 5005

vector < int > edges[maxN];
int pa[maxN];
bool used[maxN];

void dfs ( int n, int p ){
    used[n] = true;
    pa[n] = p;
    for ( auto i: edges[n] ){
        if ( used[i] )
            continue;
        dfs ( i, p );
    }
}

int main(){
    ios::sync_with_stdio ( false );
    cin.tie ( 0 );
    cout.tie ( 0 );

    int n, m, s, u, v;
    cin >> n >> m >> s;
    while ( m-- ){
        cin >> u >> v;
        edges[u].push_back ( v );
    }

    dfs ( s, s );
    for ( int i = 1 ; i <= n ; i++ ){
        if ( pa[i] == 0 ){
            memset ( used, 0, sizeof used );
            dfs ( i, i );
        }
    }

    set < int > lib;
    for ( int i = 1 ; i <= n ; i++ ){
        if ( pa[i] != s )
            lib.insert ( pa[i] );
    }

    cout << lib.size() << '\n';
}
