                    else{
                        str[i][j] = str[i - 1][j];
                        str[i][j].S = 'S';
                    }
                    str[i][j].F++;
                }
            }
        }

        data = "";
        x = y = n;
        while ( str[x][y].S != ' ' ){
            data += str[x][y].S;
            str[x][y].S == 'S' ? x-- : y--;
        }
        reverse ( data.begin(), data.end() );
        cout << "Case #" << tms << ": " << data << '\n';
    }
}
