			// 
			version.push_back ( seg );
		}
		else if ( type == 2 ){
			cin >> l >> r;
			cout << query ( l, r, 1, n, seg ) << '\n';
		}
		else{
			//  k
			cin >> in;
			seg = version[in];
		}
	}
}
