int main(){
    ios::sync_with_stdio ( false );
    cin.tie ( 0 );
    cout.tie ( 0 );

    int t, n;
    string str;
    cin >> t;
    while ( t-- && cin >> n >> str ){
        for ( auto i: str )
            cout << ( i == 'S' ? 'E' : 'S' );
        cout << '\n';
    }
}
