```cpp
// by. MiohitoKiri5474
#include<bits/stdc++.h>

using namespace std;

int input[maxN], l;
char out[maxN][2], print[maxN];

bool check ( int idx, char now ){
    if ( idx == l ){
        cout << print << '\n';
        return true;
    }
    for ( int i = 0 ; i < 2 ; i++ ){
        if ( out[idx][i] != now )
            continue;
        print[idx] = out[idx][i];
        if ( idx == l - 1 )
            print[idx + 1] = out[idx][!i];
        return check ( idx + 1, out[idx][!i] );
    }
    return false;
}

int main() {
    ios::sync_with_stdio ( false );
    cin.tie ( 0 );
    cout.tie ( 0 );

    int t, n, idx;
    set < int > prime;
    map < int, char > lib;
    cin >> t;
    for (int i = 1; i <= t; i++){
        cin >> n >> l;
        memset ( print, 0, sizeof print );
        prime.clear();
        lib.clear();
        cout << "Case #" << i << ": ";
        for ( int i = 0 ; i < l ; i++ ){
            cin >> input[i];
            for ( int j = 2 ; j <= n ; j++ ){
                if ( input[i] % j == 0 ){
                    prime.insert ( j );
                    prime.insert ( input[i] / j );
                    break;
                }
            }
        }

        idx = 0;
        for ( auto i: prime )
            lib[i] = 'A' + idx++;
        for ( int i = 0 ; i < l ; i++ )
            for ( int j = 2 ; j <= n ; j++ )
                if ( input[i] % j == 0 ){
                    out[i][0] = lib[j];
                    out[i][1] = lib[input[i] / j];
                    break;
                }
        for ( int i = 0 ; i < 2 ; i++ )
            if ( check ( 0, out[0][i] ) )
                break;
    }
    return 0;
}
